var dataBencana = [];
function getbsmiinfobencana(){ //console.log("cek2");
  $.ajax({url: "https://cors.bsmijatim.workers.dev/?https%3A%2F%2Ft.me%2Fs%2Finfo_bencana",  success: function(result){
    //console.log(result);
    var el = $( '<div></div>' );
    el.html(result);
    //result = el.getElementsByTagName("tgme_widget_message_wrap");
    result = $('.tgme_widget_message_wrap', el)
    $.each(result, function(index, val) {
      var msg = val.children[0].children[1].children[2].innerHTML;//console.log(msg);
      var img = val.children[0].children[1].children[3].href;//console.log(img);
      dataBencana[index] = {
          //"videoId":val.children[1].innerHTML,
          "msg":msg,
          "img":img,
          "datahtml":val.innerHTML
          };
      })
    sendQuerybsmiinfobencana();
  }});
}

