function menucabang()
{
    var cabang = ["BSMI Surabaya ","BSMI Sidoarjo ","BSMI KAB Mojokerto ","BSMI KOTA Mojokerto ","BSMI Lamongan ","BSMI Gresik ","BSMI Tuban ","BSMI Bojonegoro ","BSMI KOTA Pasuruan ","BSMI KAB Pasuruan ","BSMI Bangkalan ","BSMI Sampang ","BSMI Pamekasan ","BSMI Sumenep ","BSMI KOTA Probolinggo ","BSMI KAB Probolinggo ","BSMI Jember ","BSMI Lumajang ","BSMI Bondowoso ","BSMI Situbondo ","BSMI Banyuwangi ","BSMI Jombang ","BSMI KOTA Malang ","BSMI KAB Malang ","BSMI Batu ","BSMI KOTA Kediri ","BSMI KAB Kediri ","BSMI KOTA Blitar ","BSMI KAB Blitar ","BSMI Tulungagung ","BSMI KOTA Madiun ","BSMI KAB Madiun ","BSMI Ngawi ","BSMI Magetan ","BSMI Ponorogo ","BSMI Pacitan ","BSMI Trenggalek ","BSMI Nganjuk"];
    var data = "";
    cabang.forEach((item)=>{
    	//console.log(item)
		data+= "<div class='item col-6 col-sm-6 col-md-6 col-lg-3'><a href='https://www.bsmijatim.org/p/search.html?q="+item+"'>"+item+"</a></div>";
	})
	$('.menu-cabang').replaceWith(data)
}

$(document).ready(function(){
	menucabang();
	var dropdown = $(".search-dropdown");
	var toogleBtn = $(".search-dropdown .dropdown-toggle");

	// Toggle search and close icon for search dropdown
	dropdown.on("show.bs.dropdown", function(e){
		toogleBtn.toggleClass("d-none");
	});
	dropdown.on("hide.bs.dropdown", function(e){
		toogleBtn.addClass("d-none");
		toogleBtn.first().removeClass("d-none");
	});
	$(".search-dropdown .dropdown-menu").on("click", function(e){
		e.stopPropagation()
	});
});

